package Lec3;

public class Char_Data_Demo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char ch = 'a';
		int x = (ch);
		System.out.println(x);
		System.out.println((int) (ch));
		ch++;// ch = (char)(ch +1);
		System.out.println(ch);
		ch -= 1;//  ch = (char)(ch +1);
		System.out.println(ch);

	}

}
